CREATE DEFINER = CURRENT_USER TRIGGER `quasar`.`str_playersitems_AFTER_INSERT` AFTER INSERT ON `str_playersitems` FOR EACH ROW
BEGIN
	IF NOT EXISTS (	SELECT *
					FROM str_qsrloadouts qsr
					WHERE steam_id = NEW.steam_id ) 
	AND NOT EXISTS 
				  ( SELECT *
                    FROM str_tfloadouts tf
                    WHERE steam_id = NEW.steam_id) THEN
	BEGIN
		DECLARE l_id INT DEFAULT 1;
		DECLARE c_id INT DEFAULT 1;
		generate_blank_inventory: LOOP
			IF (l_id < 4) THEN
				INSERT INTO str_qsrloadouts (steam_id, loadout_id) VALUES (NEW.steam_id, l_id) ON DUPLICATE KEY UPDATE steam_id = steam_id, loadout_id = loadout_id;
