						ITERATE generate_class_inventory;
					END IF;
					LEAVE generate_class_inventory;
					END LOOP;
				SET l_id = l_id + 1;
				SET c_id = 1;
				ITERATE generate_blank_inventory;
			END IF;
			LEAVE generate_blank_inventory;
			END LOOP;
		END;
	END IF;
END;$$


